import buildClient from "../lib/build/web-app/vite/build-client.js";
import runAppServer from "../lib/build/web-app/vite/run-app-server.js";
import { chalk } from "../lib/util/chalk.js";

/**
 * Entry point for this script
 */
export default async function run() {
  process.env.NODE_ENV = "development";

  // Build the client in watch mode. The Client build IS ONLY made so we can see
  // the generated files to aid in debugging.
  buildClient();

  console.warn(chalk.yellowBrightBold("Starting dev server..."));
  await runAppServer();
}
