import shell from "shelljs";

/**
 * This checks if the current git context has access to the specified remote
 * repository.
 */
export async function gitCanAccessRemote(remote: string) {
  const checkRemoteAccessProcess = shell.exec(`git ls-remote ${remote}`);

  if (checkRemoteAccessProcess.code !== 0) {
    return false;
  }

  return true;
}
