import shell from "shelljs";

/**
 * This checks the current git repository to see if it has uncomitted changes
 * lingering. This check is necessary before some process can be executed
 * safely.
 */
export async function gitHasChanges() {
  return (
    shell.exec("git status --porcelain=v1 2>/dev/null | wc -l | grep 0", {
      silent: true,
    }).code !== 0
  );
}
