import inquirer from "inquirer";
import { stripIndent } from "common-tags";

export async function promptTextInput(
  message: string,
  defaultValue?: string
): Promise<string> {
  const { result } = await inquirer.prompt<{ result: string }>([
    {
      type: "input",
      name: "result",
      message: stripIndent`${message}`,
      default: defaultValue,
    },
  ]);

  return result;
}
