/**
 * Checks if the requested package is available to the target project.
 */
export async function isPackageAvailable(packageName: string) {
  try {
    await import(packageName);
    return true;
  } catch (err) {
    console.error(
      "Requested package is not available in this current project:",
      packageName
    );
    return false;
  }
}
