import path from "path";
import url from "url";

const __dirname = url.fileURLToPath(new URL(".", import.meta.url));

/**
 * This is a path.resolve but always uses the devops project path as the base.
 */
export async function resolveFromDevops(...args: string[]) {
  const devopsPath = path.resolve(__dirname, "../../..");
  return path.resolve(devopsPath, ...args);
}
